import React, { useState, useEffect } from 'react';
const images = ["../Images/download.jpeg", "../Images/download2.jpeg", "../Images/atoz_assessment.jpg"]

function Slider() {
    const [index, setIndex] = useState(0)

    useEffect(() => {
        setTimeout(() => {
            let newInd = index
            newInd++
            if (newInd == images.length) newInd = 0

            setIndex(newInd)
         }, 1000)
    }, [index])

    return (
        <div>
            <img src={require(images[0])}/>
        </div>
    );
}

export default Slider;
