import React, { useState, useEffect } from 'react';
import "./Slider.css"

const images = [require("../Images/download.jpeg"),require("../Images/download 2.jpeg"), require("../Images/atoz_assessment.jpg")]

function Slider() {
    const [index, setIndex] = useState(0)

    useEffect(() => {
        setTimeout(() => {
            let newInd = index
            newInd++
            if (newInd === images.length-1) {
                newInd--;
                images.push(images[0])
                images.splice(0,1)
            }

            setIndex(newInd)
         }, 3000)
    }, [index])

    function giveClassName() {
        if (index === 1) return "up1"
        if (index === 2) return "up2"
        return ""
    }

    return (
        <div className='slideDiv'>
            {images.map((image, myInd) => (
                <img src={images[myInd]} className={giveClassName(myInd)} alt='hi'/>
            ))}
        </div>
    );
}

export default Slider;
